import {
  DeleteOutlined,
  EditOutlined,
  EllipsisOutlined,
  PlusOutlined,
  SettingOutlined,
} from '@ant-design/icons';
import {
  Button,
  Card,
  Image,
  Row,
  Col,
  Menu,
  Dropdown,
  Popconfirm,
  Upload,
  message,
} from 'antd';
import { useEffect } from 'react';
import AddModal from './AddModal'; // 新增
import SetMl from './setML'; // 目录设置
import SetSC from './setSC';

// store
import AddStore from './store/AddStore';
import MlStore from './store/MlStore';
import ScStore from './store/ScStore';

import './index.less';
import { observer } from 'mobx-react';
import HomeStore from './store/HomeStore';
import Zxby from './zxby';
import ZxbyStore from './store/ZxbyStore';
import Tg from './tg';
import TgStore from './store/TgStore';

const COL_SPAN = 6;

const Daby = observer(() => {
  useEffect(() => {
    HomeStore.findAll();
  }, []);

  const showModal = () => {
    AddStore.setModalVisible(true);
  };

  const menu = (data: { id: string }) => {
    return (
      <Menu>
        <Menu.Item
          key="ml"
          onClick={() => MlStore.setModalVisibleAndId(true, data.id)}
        >
          目录
        </Menu.Item>
        <Menu.Item
          key="sc"
          onClick={() => ScStore.setModalVisibleAndId(true, data.id)}
        >
          素材
        </Menu.Item>
        <Menu.Item
          key="zxby"
          onClick={() => ZxbyStore.setModalVisibleAndId(true, data.id)}
        >
          编研
        </Menu.Item>
        <Menu.Item onClick={() => TgStore.setModalVisibleAndId(true, data.id)}>
          统稿
        </Menu.Item>
        {/* <Divider dashed /> */}
        <Menu.Divider />
        <Menu.Item key="setFm">
          <Upload
            name="file"
            action={`/api/daby/upload/${data.id}?type=fm`}
            showUploadList={false}
            onChange={(info) => {
              if (info.file.status !== 'uploading') {
                console.log(info.file, info.fileList);
              }
              if (info.file.status === 'done') {
                message.success(`${info.file.name} 文件上传成功`);
                HomeStore.findAll();
              } else if (info.file.status === 'error') {
                message.error(`${info.file.name} file upload failed.`);
              }
            }}
          >
            封面
          </Upload>
        </Menu.Item>
        <Menu.Item key="setBf">
          <Upload
            name="file"
            action={`/api/daby/upload/${data.id}?type=bf`}
            showUploadList={false}
            onChange={(info) => {
              if (info.file.status !== 'uploading') {
                console.log(info.file, info.fileList);
              }
              if (info.file.status === 'done') {
                message.success(`${info.file.name} 文件上传成功`);
                HomeStore.findAll();
              } else if (info.file.status === 'error') {
                message.error(`${info.file.name} file upload failed.`);
              }
            }}
          >
            背封
          </Upload>
        </Menu.Item>
        <Menu.Divider />
        <Menu.Item
          key="dzs_pub"
          onClick={() => {
            // ScStore.setModalVisibleAndId(true, data.id)
            TgStore.handlePdf(data.id);
          }}
        >
          下载电子书
        </Menu.Item>
      </Menu>
    );
  };

  return (
    <div style={{ width: '98%', marginLeft: '1%', marginTop: '20px' }}>
      <Row gutter={24}>
        {HomeStore.list.map((item, index) => (
          <Col span={COL_SPAN} key={index} style={{ marginTop: '10px' }}>
            <Card
              style={{ width: '100%' }}
              key={item.id}
              title={item.mc}
              actions={[
                <a key={`setting_${index}`}>预览</a>,
                <Popconfirm
                  style={{ height: '22px' }}
                  title="是否要删除该条记录？"
                  onConfirm={() => {
                    HomeStore.delete(item.id);
                    HomeStore.findAll();
                  }}
                >
                  <a key={`delete_${index}`}>删除</a>
                </Popconfirm>,
                <Dropdown overlay={menu(item)}>
                  <EllipsisOutlined key="ellipsis" />
                </Dropdown>,
              ]}
            >
              <Row gutter={8}>
                <Col span={12}>
                  <Image
                    width={'100%'}
                    height={200}
                    src={'/api/daby/view/' + item.id + '?type=fm'}
                    fallback="data:image/png;base64,"
                  />
                </Col>
                <Col span={12}>
                  <Image
                    width={'100%'}
                    height={200}
                    src={'/api/daby/view/' + item.id + '?type=bf'}
                    fallback="data:image/png;base64,"
                  />
                </Col>
              </Row>
            </Card>
          </Col>
        ))}
        <Col span={COL_SPAN} style={{ marginTop: '10px' }}>
          <Button type="dashed" className={'newButton'} onClick={showModal}>
            <PlusOutlined /> 建档案编研
          </Button>
        </Col>
      </Row>
      <AddModal />
      <SetMl />
      <SetSC />
      <Zxby />
      <Tg />
    </div>
  );
});

export default Daby;
